mapper.update:
update downtime 
	set
          down_reason = #{downReason} , 
          down_manager = #{downManager} , 
          remark = #{remark} 
 where down_code = #{downCode}

mapper.select:
  		select f.fac_code, fac_name, to_char(f.fac_date, 'YYYY-MM-DD') as fac_date ,f.pro_code, p.pro_name, f.fac_status, f.wash_cycle, f.ins_cycle, 
		to_char(f.last_inspection, 'YYYY-MM-DD') as last_inspection, f.fac_manager, f.fac_function, f.fac_picture
		from facility f join process p on(f.pro_code = p.pro_code)

  		select f.fac_name, f.fac_code , d.down_code, d.down_reason, d.stop_date
		from facility f full outer join downtime d on(f.fac_code = d.fac_code) full outer join inspection i on(d.down_code = i.down_code)
		where down_reason = '점검' and ins_code is null order by down_code desc
